    def testnormalizeAddressString(self):
        self.assertEqual(mt.normalizeAddressString("Karlovy  Vary,  Mrkvičkova  1270"), "Karlovy Vary, Mrkvičkova 1270", "Více mezer je chápáno jako jedna")
